import { useNavigation } from "@react-navigation/native";
import { StyleSheet, SafeAreaView, View, Text, Image, Pressable} from "react-native";
import { NativeStackNavigationProp } from "@react-navigation/native-stack";
import { CheckScreenNavigationprop } from "../../App";



const PlaceHolderImage = require('../../assets/vector-1-welcome.png');
/* Solution for no need to type check the routes and their parameters
interface NavigationProps{
    navigation: NativeStackNavigationProp<any>
}
*/
export default function GetStarted(){
    //Const is declared to use the useNavigation hook to have access on "navigation" object
    const navigation = useNavigation<CheckScreenNavigationprop>(); 
    return(
        <SafeAreaView>
                <View style={styles.container}>
                    <View style={styles.imageContainer}>
                        <Image style={styles.image} source={PlaceHolderImage}/>
                    </View>
                </View>
            <Text style={styles.textHeader}>Welcome</Text>
            <Text style={styles.textMultiline}>Record, track, and collect payments from clients with ease. </Text>

          
            <View style={styles.buttonContainer}>
                <Pressable style={styles.button} onPress={()=>navigation.navigate('Login')}>
                        <Text style={styles.buttonLabel}>Get Started</Text>
                </Pressable>
            </View>
            

            <View style={styles.widgetContainer}>
                <View style={styles.widget}>
                    <Text style={styles.widgetLabel}>Thomas just saved $37.52 by lowering his ADT Security bill.</Text>
                </View>    
            </View>
        
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    container:{
        flex: 1,
        alignItems:'center', 
        justifyContent: 'center', 
    },

    textHeader:{
        fontSize:55,
        fontWeight: 'bold', 
        paddingTop: 360, 
        paddingHorizontal: 35, 
        color: '#203949'
    }, 

    textMultiline:{
        fontSize: 23,
        flexWrap: 'wrap',
        flexDirection: 'row', 
        paddingTop: 7, 
        paddingHorizontal: 35, 
        color: '#707070'

    },

    imageContainer:{
        width: '100%', 
        height: '100%', 
        flex: 1
    },
    
    image:{
        width: 450, 
        height: 500
    }, 
    
    button:{
        borderRadius: 10 ,
        width: '100%', 
        height: '100%', 
        alignItems: 'center', 
        justifyContent: 'center', 
        flexDirection: 'row'
    },

    buttonLabel:{
        color: '#fff', 
        fontSize: 20
    },

    buttonContainer:{
        width: 214, 
        height: 65, 
        backgroundColor: '#707070',
        alignItems: 'center', 
        justifyContent: 'center', 
        borderRadius: 5, 
        marginLeft: 90, 
        marginTop: 138
    }, 

    widget:{
        borderRadius: 15 ,
        width: '100%', 
        height: '100%', 
        alignItems: 'center', 
        justifyContent: 'center', 
        flexDirection: 'row', 
    },

    widgetLabel:{
        color: '#707070', 
        fontSize: 12
    },

    widgetContainer:{
        width: 300, 
        height: 80, 
        backgroundColor: '#F5F7F9',
        alignItems: 'center', 
        justifyContent: 'center', 
        borderRadius: 5, 
        marginLeft: 55, 
        marginTop: 65,
    }, 

});
