import {SafeAreaView, View, Text, StyleSheet, Pressable, GestureResponderEvent} from 'react-native'

import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';

type PaymentRecordProps = {
    key:number, 
    collectorName:String; 
    transactionDate: String; 
    recentMessage: String; 

}

export default function PaymentRecordLists(props: PaymentRecordProps){
    return(
        <SafeAreaView style={styles.item}>
            <View style={styles.itemLeft}>
                <View style={styles.square}/>
                    <View style={styles.itemText}>
                        <Text style={{color:'#363636'}}>{props.collectorName}</Text>
                        <Text style={{color: '#92A0A8', fontSize: 10}}>{props.recentMessage}</Text>                                  
                    </View>
                    <View style={styles.dateContainer}>
                        <Text style={styles.dateLabel}>{props.transactionDate}</Text>        
                    </View>                        
            </View>            
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    item:{
        flex:1, 
        backgroundColor: '#F5F7F9',
        padding: 20, 
        borderRadius: 10, 
        marginBottom: 20,
        shadowColor: '#000', 
        shadowOffset: {
            width:0,
            height: 2,
        },
        shadowRadius: 4,
        elevation: 2
    },
    itemLeft:{
        flex:1, 
        flexDirection:'row',
        aligntItems: 'center', 
        flexWrap:'wrap'
    },
    square:{
        flex:.5, 
        margin: hp(1.5), 
        backgroundColor: '#92A0A8', 

    }, 
    itemText: {
        flex:1, 
        maxWidth: '80%', 
        marginRight: hp(1.5), 
        justifyContent: 'center', //vertical alignment
        alignItems: 'flex-start', 
        textAlign: 'center'
    }, 
    dateContainer: {
        width: wp(20), 
        height: hp(5),
        paddingRight: hp(2), 
        justifyContent: 'center'
    }, 
    dateLabel:{
        color: '#92A0A8', 
        fontSize: hp(1.2)
    },
});
