import { useNavigation } from "@react-navigation/native";
import React from "react";
import { SafeAreaView, Text, View, StyleSheet, Pressable, TextInput} from "react-native";
import { CheckScreenNavigationprop } from "../../App";

import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';
export default function Login(){
    const navigation = useNavigation<CheckScreenNavigationprop>(); 

    return(
        <SafeAreaView >
            <View style={styles.container}>

                <View style={styles.header}>
                    <Text style={styles.textStyleSubheader} onPress={()=>navigation.goBack()}>Back</Text>
                    <Text style={styles.textStyleHeader}>Login</Text>
                </View>

                <View style={styles.main}>

                    <View style={styles.body}>
                        <View style={styles.buttonUnfilledContainer}>
                            <Pressable style={styles.buttonUnfilled} onPress={()=>navigation.push('Register')}>
                                <Text style={styles.buttonUnfilledLabel}>No account yet? Register Now!</Text>
                            </Pressable>
                        </View>
                            <Text style={styles.textStyleSmallest} onPress={()=>alert('Coming Soon!')}>Forgot Password?</Text>
                            <TextInput placeholderTextColor="#C2C6CC" style={styles.textBoxStyle} placeholder="Enter username" ></TextInput>
                            <TextInput placeholderTextColor="#C2C6CC" style={styles.textBoxStyle} placeholder="Enter password" ></TextInput>
                        </View>
                     </View>
                <View style={styles.footer}>
                    <View style={styles.buttonContainer}>
                        <Pressable style={styles.button} onPressIn={()=>navigation.push('TellUsMoreAboutYourself')}>
                            <Text style={styles.buttonLabel}>Login</Text>
                        </Pressable>
                    </View>               
                </View>
            </View>
        </SafeAreaView>
    );

}


const styles = StyleSheet.create({
    container:{
        flex: 1,
        height: hp(100)
    },
    header:{
        flex: 0.5, 
        height:hp(50), 
        margin: hp(5)
    }, 
    main:{
        flex: 0.5, 
        //height: hp(35), 
        display: 'flex', 
        flexDirection: 'row'
    }, 
    body:{
        flex:1,
        width: wp(100), 
        paddingLeft: 25, 
        paddingRight: 25
    }, 
    footer:{
        flex:0.3, 
        height: hp(50)
    }, 
    textStyleSubheader:{
        paddingTop: 35, 
        paddingBottom: 25
    },
    textStyleHeader:{
        fontSize: 55, 
        fontWeight: 'bold', 
    },

    textStyleSmallest:{
        fontSize: 13,
        alignItems:'center',
        justifyContent:'center', 
        marginTop: 16,
        marginBottom:16, 
        paddingLeft:115
    },

    textBoxStyle:{
        width: 320, 
        height: 50, 
        paddingLeft: 20,
        borderColor: '#F0F2F4', 
        borderWidth: 2,
        marginBottom: 13, 
        color:'#363636', 
    },

    buttonContainer:{
        width: 318, 
        height: 48, 
        backgroundColor: '#2C85E7',
        alignItems: 'center', 
        justifyContent: 'center', 
        borderRadius: 5, 
        marginTop: 30
    }, 

    button:{
        borderRadius: 10 ,
        width: '100%', 
        height: '100%', 
        alignItems: 'center', 
        justifyContent: 'center', 
        flexDirection: 'row'
    },

    buttonLabel:{
        color: '#fff', 
    },

    buttonUnfilledContainer:{
        width: wp(50), 
        height: hp(15), 
        backgroundColor: '#fff',
        alignItems: 'center', 
        justifyContent: 'center', 
        borderRadius: 5, 
        borderColor: '#F0F2F4',
        borderWidth: 2, 
    }, 

    buttonUnfilled:{
        borderRadius: 10 ,
        width: '100%', 
        height: '100%', 
        alignItems: 'center', 
        justifyContent: 'center', 
        flexDirection: 'row'
    },

    buttonUnfilledLabel:{
        color: '#4A5B6B', 
    }, 

});



