import {SafeAreaView, View, Text, StyleSheet, Pressable, GestureResponderEvent} from 'react-native'
import {widthPercentageToDP as wp, heightPercentageToDP as hp} from 'react-native-responsive-screen';


type PaymentProps = {
    key:number, 
    itemName:String; 
    itemCollectible: number; 
}

export default function DuePaymentList(props: PaymentProps){
    return(
        <SafeAreaView style={styles.item}>
            <View style={styles.itemLeft}>
                <View style={styles.square}/>

                <View style={styles.itemText}>
                    <Text style={{color:'#363636'}}>{props.itemName}</Text>
                    <Text style={{color: '#92A0A8'}}>Php {props.itemCollectible}</Text>                                  
                </View>

                <View style={styles.buttonContainer}>
                    <Pressable style={styles.button} onPress={()=>alert("hello")}>
                        <Text style={styles.buttonLabel}>Pay</Text>
                    </Pressable>
                </View>
            </View>

        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
    item:{
        flex: 1,
        backgroundColor: '#F5F7F9',
        padding: 20, 
        borderRadius: 10, 
        marginBottom: hp(2),
        shadowColor: '#000', 
        shadowOffset: {
            width:0,
            height: 2,
        },
        shadowRadius: 4,
        elevation: 2
    },
    itemLeft:{
        flex: 1,
        flexDirection:'row',
        aligntItems: 'center', 
        flexWrap:'wrap'
    },
    square:{
        flex:0.8,
        margin: hp(2), 
        backgroundColor: '#92A0A8', 
        borderRadius: 5
    }, 
    itemText: {
        flex:1, 
        maxWidth: '80%', 
        justifyContent: 'center', 
        alignItems: 'flex-start',
        textAlign: 'left'
    }, 
    buttonContainer: {
        flex:1,
        width: wp(20), 
        height: hp(6.5),
        maxHeight: '100%', 
        maxWidth: '100%',
        backgroundColor:'#2C85E7',
        justifyContent:'space-evenly',
        borderRadius: 5
    }, 
    button:{
        width: '100%', 
        height: '100%', 
        alignItems: 'center', 
        justifyContent: 'center', 
        flexDirection: 'row'
    },
    buttonLabel:{
        color: '#fff', 
        fontSize: hp(1.5),
    },
});
